// import React, { useState, useEffect } from 'react';
import { useEffect, useState } from 'react';
import './PlayersPage.css';

interface Player {
  id: string;
  name: string;
  country: string;
  rank: number;
  points: number;
}

interface ApiResponse {
  rankings?: Player[];
  players?: Player[];
  data?: any;
  [key: string]: any;
}

const PlayersPage: React.FC = () => {
  const [players, setPlayers] = useState<Player[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [debugInfo, setDebugInfo] = useState<string>('');

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        setLoading(true);
        setError(null);
        setDebugInfo('Starting API call...');

        const apiKey = 'mI0K6O5QLl4C7PBOxGl46rBnfaemkzs0SOVokjP';

        // Updated endpoints based on official Sportradar docs
        const possibleUrls = [
          // Official World Golf Ranking endpoint (most likely correct)
          `https://api.sportradar.com/golf/trial/v3/en/rankings/owgr.json?api_key=${apiKey}`,
          // Alternative rankings endpoints
          `https://api.sportradar.com/golf/trial/v3/en/rankings/wgr/2025.json?api_key=${apiKey}`,
          `https://api.sportradar.com/golf/trial/v3/en/rankings.json?api_key=${apiKey}`,
          // Player endpoints that might include rankings
          `https://api.sportradar.com/golf/trial/v3/en/players/2025/pga.json?api_key=${apiKey}`,
        ];

        let lastError = null;

        for (const url of possibleUrls) {
          try {
            console.log(`Trying URL: ${url}`);
            setDebugInfo(`Trying: ${url.split('?')[0]}`);

            const response = await fetch(url, {
              method: 'GET',
              headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'User-Agent': 'GolfApp/1.0',
              },
              mode: 'cors', // Enable CORS
            });

            console.log(`Response status: ${response.status}`);

            if (response.status === 403) {
              throw new Error('API Key Invalid: Your Sportradar API key may be expired or invalid');
            }

            if (response.status === 404) {
              console.log(`Endpoint not found: ${url}`);
              lastError = new Error(`Endpoint not found: ${url}`);
              continue; // Try next URL
            }

            if (!response.ok) {
              const errorText = await response.text();
              console.error(`API Error:`, errorText);
              throw new Error(`HTTP ${response.status}: ${errorText.substring(0, 200)}`);
            }

            const data: ApiResponse = await response.json();
            console.log('Raw API Response:', data);
            console.log('Response keys:', Object.keys(data));

            // Try to extract players from different possible response structures
            let playersData: Player[] = [];

            // Check for Official World Golf Ranking structure
            if (data.rankings && Array.isArray(data.rankings)) {
              playersData = data.rankings.map((player: any, index: number) => ({
                id: player.id || `player-${index + 1}`,
                name: player.name || 'Unknown Player',
                country: player.country || player.nationality || 'Unknown',
                rank: player.rank || player.position || index + 1,
                points: player.points || player.average_points || 0
              }));
            } else if (data.players && Array.isArray(data.players)) {
              playersData = data.players.map((player: any, index: number) => ({
                id: player.id || `player-${index + 1}`,
                name: player.name || player.full_name || 'Unknown Player',
                country: player.country || player.nationality || 'Unknown',
                rank: index + 1, // Assume they're in ranking order
                points: player.points || 0
              }));
            } else if (Array.isArray(data)) {
              playersData = data.map((player: any, index: number) => ({
                id: player.id || `player-${index + 1}`,
                name: player.name || player.full_name || 'Unknown Player',
                country: player.country || player.nationality || 'Unknown',
                rank: player.rank || index + 1,
                points: player.points || 0
              }));
            } else {
              // Look for any array in the response
              const possibleArrays = Object.entries(data)
                .filter(([key, value]) => Array.isArray(value))
                .map(([key, value]) => ({ key, value: value as any[] }));

              if (possibleArrays.length > 0) {
                const arrayData = possibleArrays[0].value;
                playersData = arrayData.map((player: any, index: number) => ({
                  id: player.id || `player-${index + 1}`,
                  name: player.name || player.full_name || 'Unknown Player',
                  country: player.country || player.nationality || 'Unknown',
                  rank: player.rank || index + 1,
                  points: player.points || 0
                }));
                setDebugInfo(`Found ${playersData.length} players in '${possibleArrays[0].key}' field`);
              } else {
                console.log('Unexpected data structure:', data);
                setDebugInfo(`Unexpected response format. Keys: ${Object.keys(data).join(', ')}`);
                throw new Error(`No player array found. Available keys: ${Object.keys(data).join(', ')}`);
              }
            }

            if (playersData.length === 0) {
              throw new Error('No player data found in response');
            }

            // Clean and validate the data
            const validPlayers = playersData
              .filter(player => player && player.name && player.name !== 'Unknown Player')
              .slice(0, 20); // Top 20 players

            if (validPlayers.length === 0) {
              throw new Error('No valid player data after filtering');
            }

            setPlayers(validPlayers);
            setDebugInfo(`âœ… Successfully loaded ${validPlayers.length} players from ${url.split('?')[0]}`);
            setError(null);
            return; // Success, exit the loop

          } catch (err: any) {
            console.error(`Error with URL ${url}:`, err);
            lastError = err;

            // Don't continue if it's an auth error
            if (err.message.includes('API Key Invalid')) {
              throw err;
            }
            continue; // Try next URL
          }
        }

        // If we get here, all URLs failed
        throw lastError || new Error('All API endpoints failed');

      } catch (err: any) {
        console.error('Final API Error:', err);

        let errorMessage = 'Unknown error occurred';

        if (err.message.includes('API Key Invalid')) {
          errorMessage = 'ðŸ”‘ API Key Issue: Your Sportradar API key appears to be invalid or expired. Check your account at developer.sportradar.com';
        } else if (err.message.includes('CORS')) {
          errorMessage = 'ðŸŒ CORS Error: Browser blocked the request. You may need to use a backend proxy or try a different approach.';
        } else if (err.message.includes('Failed to fetch') || err.message.includes('NetworkError')) {
          errorMessage = 'ðŸŒ Network Error: Unable to reach Sportradar API. Check your internet connection.';
        } else if (err.message.includes('No player')) {
          errorMessage = `ðŸ“Š Data Format Issue: ${err.message}`;
        } else {
          errorMessage = `âŒ API Error: ${err.message}`;
        }

        setError(errorMessage);
        setDebugInfo(`Error: ${err.message}`);
        setPlayers([]);

      } finally {
        setLoading(false);
      }
    };

    fetchPlayers();
  }, []);

  const renderPlayerCard = (player: Player | null, index: number) => (
    <div key={index} className="player-card">
      {player ? (
        <>
          <div className="player-rank">#{player.rank}</div>
          <h3 className="player-name">{player.name}</h3>
          <p className="player-country">{player.country}</p>
          <div className="player-points">
            {typeof player.points === 'number' && player.points > 0
              ? `${player.points.toFixed(3)} points`
              : 'N/A points'
            }
          </div>
        </>
      ) : (
        <>
          <div className="player-rank placeholder">#--</div>
          <h3 className="player-name placeholder">Loading...</h3>
          <p className="player-country placeholder">--</p>
          <div className="player-points placeholder">-- points</div>
        </>
      )}
    </div>
  );

  return (
    <div className="players-page">
      <header className="page-header">
        <h1>World Golf Rankings</h1>
        <p>Current top players in professional golf</p>
      </header>

      {/* Debug Info */}
      {debugInfo && (
        <div className="debug-info">
          <small>Debug: {debugInfo}</small>
        </div>
      )}

      {loading && (
        <div className="loading-container">
          <p>Loading players from Sportradar API...</p>
        </div>
      )}

      {error && (
        <div className="error-container">
          <h3>ðŸš¨ API Issue</h3>
          <p><strong>Error:</strong> {error}</p>
          <details>
            <summary>Troubleshooting Steps</summary>
            <ul>
              <li><strong>Check API Key:</strong> Visit <a href="https://developer.sportradar.com" target="_blank">developer.sportradar.com</a> to verify your API key status</li>
              <li><strong>Try New Key:</strong> Your trial key might be expired - create a new account if needed</li>
              <li><strong>Check Quota:</strong> You might have exceeded your API call limit</li>
              <li><strong>Verify Endpoints:</strong> Sportradar may have changed their endpoint URLs</li>
              <li><strong>Browser Console:</strong> Check for detailed error messages in developer tools</li>
            </ul>
          </details>
        </div>
      )}

      <div className="players-grid">
        {Array(20).fill(null).map((_, index) => 
          renderPlayerCard(players[index] || null, index)
        )}
      </div>

      <footer className="page-footer">
        <p>
          {players.length > 0 
            ? `Showing ${players.length} players â€¢ Data provided by Sportradar API`
            : 'Data provided by Sportradar API'
          }
        </p>
      </footer>
    </div>
  );
};

