import React, { useEffect, useState } from "react";
import "./Tournaments.css";

interface Tournament {
  id: string;
  name: string;
  event_type: string;
  purse: number;
  currency: string;
  points: number;
  start_date: string;
  end_date: string;
  course_timezone: string;
  network: string;
  total_rounds: number;
  status: string;
  parent_id: string;
  scoring_system: string;
  venue: {
    id: string;
    name: string;
    city: string;
    state: string;
    zipcode: string;
    country: string;
    latitude: string;
    longitude: string;
  };
}

interface TournamentSchedule {
  tour: {
    id: string;
    alias: string;
    name: string;
  };
  season: {
    id: string;
    year: number;
  };
  tournaments: Tournament[];
}

const TournamentsPage: React.FC = () => {
  const [tournaments, setTournaments] = useState<Tournament[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [sortBy, setSortBy] = useState<string>("start_date");

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await fetch("/tournament_schedule.json");
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data: TournamentSchedule = await response.json();
        const openTournaments = data.tournaments.filter(
          (tournament) => tournament.status.toLowerCase() === "schedule"
        );
        setTournaments(openTournaments);
      } catch (error) {
        setError((error as Error).message);
      } finally {
        setLoading(false);
      }
    };

    fetchTournaments();
  }, []);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      month: "short",
      day: "numeric",
      year: "numeric",
    });
  };

  const formatCurrency = (amount: number, currency: string) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: currency,
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const filteredAndSortedTournaments = tournaments
    .filter(
      (tournament) =>
        tournament.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tournament.venue.city
          .toLowerCase()
          .includes(searchTerm.toLowerCase()) ||
        tournament.venue.state.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      switch (sortBy) {
        case "start_date":
          return (
            new Date(a.start_date).getTime() - new Date(b.start_date).getTime()
          );
        case "purse":
          return b.purse - a.purse;
        case "name":
          return a.name.localeCompare(b.name);
        case "points":
          return b.points - a.points;
        default:
          return 0;
      }
    });

  if (loading) {
    return (
      <div className="tournaments-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading tournaments...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="tournaments-page">
        <div className="error-container">
          <div className="error-icon">⚠️</div>
          <h3>Error loading tournaments</h3>
          <p>{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="retry-button"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="tournaments-page">
      <div className="page-header">
        <div className="header-content">
          <h1>Open Tournaments</h1>
          <p className="subtitle">
            {filteredAndSortedTournaments.length} tournament
            {filteredAndSortedTournaments.length !== 1 ? "s" : ""} available
          </p>
        </div>

        <div className="controls">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search tournaments, cities, or states..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            <span className="search-icon">🔍</span>
          </div>

          <div className="sort-container">
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="sort-select"
            >
              <option value="start_date">Sort by Date</option>
              <option value="purse">Sort by Purse</option>
              <option value="name">Sort by Name</option>
              <option value="points">Sort by Points</option>
            </select>
          </div>
        </div>
      </div>

      {filteredAndSortedTournaments.length === 0 ? (
        <div className="no-tournaments">
          <div className="no-tournaments-icon">🏌️‍♂️</div>
          <h3>No tournaments found</h3>
          <p>Try adjusting your search criteria</p>
        </div>
      ) : (
        <div className="tournaments-grid">
          {filteredAndSortedTournaments.map((tournament) => (
            <div key={tournament.id} className="tournament-card">
              <div className="card-header">
                <h2 className="tournament-name">{tournament.name}</h2>
                <span className="status-badge">Open</span>
              </div>

              <div className="tournament-details">
                <div className="detail-row">
                  <span className="detail-label">📅 Dates</span>
                  <span className="detail-value">
                    {formatDate(tournament.start_date)} -{" "}
                    {formatDate(tournament.end_date)}
                  </span>
                </div>

                <div className="detail-row">
                  <span className="detail-label">💰 Purse</span>
                  <span className="detail-value purse">
                    {formatCurrency(tournament.purse, tournament.currency)}
                  </span>
                </div>

                <div className="detail-row">
                  <span className="detail-label">🏆 Points</span>
                  <span className="detail-value points">
                    {tournament.points.toLocaleString()}
                  </span>
                </div>

                <div className="detail-row">
                  <span className="detail-label">📺 Network</span>
                  <span className="detail-value">{tournament.network}</span>
                </div>

                <div className="detail-row">
                  <span className="detail-label">🏌️ Event Type</span>
                  <span className="detail-value">{tournament.event_type}</span>
                </div>

                <div className="detail-row">
                  <span className="detail-label">🌊 Rounds</span>
                  <span className="detail-value">
                    {tournament.total_rounds}
                  </span>
                </div>
              </div>

              <div className="venue-info">
                <div className="venue-header">📍 Venue</div>
                <div className="venue-name">{tournament.venue.name}</div>
                <div className="venue-location">
                  {tournament.venue.city}, {tournament.venue.state}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default TournamentsPage;
